mapper.insert:
insert into t_media
set iuser = #{iuser}
, title = #{title}
, date = #{date}
, genrepk = #{genrepk}
, is_saw = #{isSaw}

    , comment = null
    , star = null



        , comment = #{comment}


        , star = #{star}

mapper.update:
UPDATE t_media
SET

    title = #{title}


    , date = #{date}


    , genrepk = #{genrepk}


    , is_saw = #{isSaw}


    , comment = null
    , star = null


    , comment = #{comment}
    , star = #{star}

where iuser = #{iuser}
and imedia = #{imedia}
and del = 0

update t_media
set del = 1
where imedia = #{imedia}
and iuser = #{iuser}
and del = 0

mapper.select:
SELECT imedia
FROM t_media
WHERE iuser = #{iuser}
AND imedia = #{imedia}
AND del = 0

select m.imedia, p.pic, m.date,
COUNT(distinct m.imedia) as mediaCnt
from t_media m
join t_media_pics p
ON m.imedia = p.imedia
WHERE MONTH (m.date) = #{month}
AND YEAR (m.date) = #{year}
and m.iuser = #{iuser}
and m.del = 0
GROUP BY m.date

SELECT imedia,title, date, is_saw AS isSaw,
if(is_saw = 1, star, NULL) as star,
if(is_saw = 1, comment, NULL) as comment, genrepk
from t_media
WHERE iuser = #{iuser}
AND imedia = #{imedia}
and del = 0
